export default async function handler(
  req: NextApiRequest,
  res: NextApiResponse
) {
  if (req.method === "GET") {
    const { input } = req.query;
    if (typeof input !== "string") {
      return res
        .status(400)
        .json({ success: false, message: "Input must be a string" });
