function generate() {
  // 2ì°¨ì› ë°°ì—´ì„ ëŒë©´ì„œ ìƒíƒœë¥¼ ì •í•œë‹¤.
  for (let column = 0; column < columnCount; column++) {
    for (let row = 0; row < rowCount; row++) {
      // ì™¼ìª½ ì´ì›ƒ
      let left = (column - 1 + columnCount) % columnCount;

      // ì˜¤ë¥¸ìª½ ì´ì›ƒ
